/* eslint-disable @typescript-eslint/no-explicit-any */
import { Component } from '@angular/core';
import { FormControl, FormGroup } from '@angular/forms';

@Component({
  selector: 'app-file-upload',
  templateUrl: './file-upload.component.html',
  styleUrls: ['./file-upload.component.css']
})
export class FileUploadComponent {
  fileForm: FormGroup;

  constructor() {
    this.fileForm = new FormGroup({
      gpxFile : new FormControl
    })
  }


 
  gpxFileValidator(control: FormControl): { [key: string]: any } | null { /*metoda muze vratit bud kvp object nebo null*/
    const file = control.value;
    if (file && file.name) {
      const allowedExtension = '.gpx';
      const fileExtension = file.name.split('.').pop().toLowerCase();
      if (fileExtension !== allowedExtension) {
        return { invalidExtension: true };
      }

    }
    return null;
  }
  csvFileValidator(control: FormControl): { [key: string]: any } | null  {
    const file = control.value;
    if (file && file.name) {
      const allowerExtension = '.csv';
      const fileExtension = file.name.split('.').pop().toLowerCase();
      if (fileExtension !== allowerExtension) {
        return { invalidExtension: true };
      }
    }
    return null;
  }

  onSubmit() {
    if (this.fileForm.valid) {
      console.log(1);

    }
  }
 }
