import { Component } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';

@Component({
  selector: 'app-file-upload',
  templateUrl: './file-upload.component.html',
  styleUrls: ['./file-upload.component.css']
})
export class FileUploadComponent {
  fileForm: FormGroup;

  constructor() {
    this.fileForm = new FormGroup({
      gpxFile : new FormControl
    })
  }


  gpxFileValidator(control: FormControl): { [key: string]: any } | null {
    const file = control.value;
    if (file && file.name) {
      const allowedExtension = '.gpx';
      const fileExtension = file.name.split('.').pop().toLowerCase();
      if (fileExtension !== allowedExtension) {
        return { invalidExtension: true };
      }

    }
    return null;
  }
}


<p>file-upload works!</p>

<form>
  <input class="form-control" type="text" placeholder="Name of your route" aria-label="Name your route">
  <div class="mb-3">
    <label for="formFile" class="form-label">GPX</label>
    <input class="form-control" type="file" accept=".gpx" id="formFile">
  </div>
  <div class="mb-3">
    <label for="formFile" class="form-label">CSV</label>
    <input class="form-control" type="file" accept=".csv" id="formFile">
  </div>
  <button class="btn btn-primary" type="submit">Button</button>
</form>
